/* 隐藏不需要的 */
.tbh-nav,
.layer,
.screen-outer,
.tb-footer,
.fixedtool,
.search-hots-sline,
.tbh-qr-wrapper,
.search-ft,
.tbh-corner,
.corner-inner,
.site-nav-mobile,
.site-nav-sitemap,
.site-nav-service,
.site-nav-pipe,
.site-nav-seller,
.site-nav-catalog,
#J_SiteFooter{
    display: none !important;
}

body{
    height: 100vh;
    background: white;
}

/* 搜索框 */
.J_Top{
    height: 62px;
}

.J_Cup{
    position: fixed;
    top: 50%;
