}

/* 顶部nav */
.J_Cover{
    position: fixed;
    top: 55%;
    left: 50%;
    transform: translateX(-50%);
}

.site-nav{
    background-color: white;
    border: none;
}

.site-nav-bd{
    width: 630px !important;
    background-color: white !important;
}

/*     登录页背景图片 */
    div.login-newbg {
        background-image: url("http://wx3.sinaimg.cn/large/0065PkNmgy1ffm1ccv8scj31kw0zke82.jpg") !important;
    }

    /*   页面主体,缩小+移位+毛玻璃框+盒阴影 */
    div.content-layout {
        background: rgba(255, 255, 255, 0.4);
        box-shadow: 0 0 0 1px rgba(255, 255, 255, .3) inset, 0 .5em 1em rgba(0, 0, 0, 0.6);
        border-radius: 2em;
        width: 900px !important;
        height: 490px !important;
        position: absolute;
        top: 45px;
    }

    /*   扩宽登录表单容器并更改其位置 */
    .login-box-warp {
        width: 650px;
        top: 60px;
        right: 120px;
    }

    /* 主要作用于天猫登录页的登录表单，此处对淘宝登录影响不大*/
    .bd {
        width: 650px;
    }

    #J_LoginBox {
        border-radius: 1em;
        position: absolute;
        width: 650px;
        box-shadow: 0 0 0 1px rgba(255, 255, 255, .3) inset, 0 .5em 1em rgba(0, 0, 0, 0.6);
        padding-bottom: 7px;
    }

    /*   覆盖在背景上的广域广告链接，登录提示，二维码与账号密码的切换按钮，二维码下方的'密码登录'和'注册'链接 */
    .login-adlink,
    .login-tip,
    #J_Quick2Static,
    div.login-links:nth-child(5) {
        display: none !important;
    }

    /*   显示账号密码登陆 */
    #J_StaticForm {
        display: inline-block !important;
        padding-right: 35px;
        border-right-style: dashed;
    }

    /*   将二维码登录移动至容器右上角 */
    .quick-form {
        width: 300px;
        float: right !important;
    }

:root {
	--m-bgimg: url();
	--m-bgcolor: #212121;

	
	--m-con-opacity: .85;
	
	--m-darkest: hsla(0, 0%, 0%, var(--m-con-opacity));
	--m-darkest-solid: hsl(0, 0%, 0%);
	--m-dark: hsla(0, 0%, 10%, var(--m-con-opacity));
	--m-dark-solid: hsl(0, 0%, 10%);

	--m-gray: hsla(0, 0%, 25%, var(--m-con-opacity));
	--m-gray-solid: hsl(0, 0%, 25%);
	--m-gray2: hsla(0, 0%, 50%, var(--m-con-opacity));
	--m-gray2-solid: hsl(0, 0%, 50%);

	--accent-blue: hsl(195, 100%, 42%);
	--accent-blue-light: hsla(195, 100%, 42%, .5);
	--accent-pink: hsl(340, 85%, 66%);
	--accent-pink-light: hsla(340, 85%, 66%, .5);
	--accent-yellow: hsl(33, 95%, 61%);
	--accent-yellow-light: hsla(33, 95%, 61%, .5);
	--accent-green: hsl(142, 61%, 55%);
	--accent-green-light: hsla(142, 61%, 55%, .5);

	/*容器边框色*/
	--content-border-color: #00a1d6;

	/*字体颜色*/
	--m-font-color: #fdfdfd;

	/*字体副色*/
	--m-font-subcolor: #a9b1bb;
	--m-font-subcolor2: #ddedf7;

	--content-radius: 4px;

	--shadow-dark-color: rgba(0, 0, 0, .5);
	--shadow-color: rgba(0, 0, 0, .3);

	/*content-shadow主要是每个板块的阴影，如首页的动画、番剧、国创区...*/
	--content-shadow: 0 8px 20px -4px var(--shadow-color);
	/*主要用于首页或其它分区页的横向分区导航栏（也就是首页、动画、番剧、国创、音乐...的横向长列表栏）中的划过分区后的子导航项的阴影，如动画划过还有MAD-AMV、MMD-3D这些选项阴影*/
	--shadow40: 0 8px 40px var(--shadow-color);
	/*shadow60这个阴影是用来做一些小弹窗用的，如用户信息卡片、评论中的表情选择框、评论区的勋章说明窗、视频中的标签简介*/
	--shadow60: 0 20px 60px var(--shadow-color);
	/*dialog-shadow是一些弹窗并背景变暗的阴影，如关注up主后的提示、充电提示、收藏提示*/
	--dialog-shadow: 0 20px 80px var(--shadow-dark-color);


	/*滤镜效果，专门把过于亮色的图标做处理*/
	--darken-filter: invert(1) hue-rotate(180deg) brightness(1.5);
	/*直接把图标变为白色*/
	--white-filter: contrast(0) brightness(2);
	/*这里能把粉色的图标强制变为蓝色*/
	--pink-turn-blue-filter:hue-rotate(230deg) brightness(1.12);
}

@keyframes showInfo {
	0% {
		opacity: 0;
		transform: translate(0, 40%);
	}
}
@keyframes shock {
	0% {
		transform: translate(-10%, 0px);
	}
	25% {
		transform: translate(10%, 0px);
	}
	50% {
		transform: translate(0px, 10%);
	}
	75% {
		transform: translate(0px, -10%);
	}
}
@keyframes showVideo {
	from {
		opacity: 0;
		transform: scale(.8);
	}
}
@keyframes floatDown {
	from {
		opacity: 0;
		margin-top: -20%;
	}
	30% {
		opacity: 0;
	}
}
@keyframes floatUp {
	from {
		opacity: 0;
		margin-top: 30%;
	}
	10% {
		opacity: 0;
	}
}

/***************滚动条调整****************/
.ps__rail-y,
.mCSB_draggerRail,
.ps__scrollbar-y-rail {
	background-color: transparent!important;
}

::-webkit-scrollbar,
.custom-scrollbar::-webkit-scrollbar {
	width: 6px;
	height: 6px;
}

::-webkit-scrollbar-track-piece,
::-webkit-scrollbar-corner,
.custom-scrollbar::-webkit-scrollbar-track-piece {
	background: transparent;
}

::-webkit-scrollbar-thumb,
.custom-scrollbar::-webkit-scrollbar-thumb {
	background-color: hsla(0, 0%, 50%, .7);
	border-radius: 10px;
	border: none;
}
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active {
	background-color: hsla(0, 0%, 50%, .85);
}

/***************全局****************/
html::after {
	content: '';
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: -1;
	background-color: var(--m-mask-color);
}

html,
.bili-header-m > .nav-menu {
	background-image: var(--m-bgimg)!important;
	background-color: var(--m-bgcolor)!important;
	background-attachment: fixed!important;
	background-size: cover!important;
	background-repeat: no-repeat!important;
	background-position: center!important;
}

body {
	background: transparent;
}

.bili-wrapper,
.bangumi-index-wrapper,
.main-inner,
.cinema-home-wrapper,
.container-body,
.cinema-documentary-wrapper {
	width: 1230px!important;
}

@media screen and (max-width: 1400px) {
	.bili-wrapper,
	.cinema-home-wrapper,
	.bangumi-index-wrapper,
	.main-inner,
	.container-body,
	.cinema-documentary-wrapper {
		width: 1045px!important;
	}
}

#app,
.bili-header-m {
	background-color: transparent;
}

.nav-con .mobile,
#fixed_app_download,
#elevator-mobile-app,
.gif-menu,
.mobile-link-l {
	display: none!important;
}

/*冗余样式，过后会调整，不建议把字体调整一次过放在这里*/
.t,
.txt,
.tit,
.title,
span.info,
.item,
.info,
.text,
.detail a,
.operation,
h3,
h4.name,
.social a,
.user-name a,
.headline .name,
.title .name,
.info .user .name:not([class*=vip]),
a.username:not([class*=vip]),
.name,
.ri-title,
.idc-uname,
.tag-list a:hover,
.batch,
.video-desc:not(:hover),
.user-count,
.item-title,
.more:not(:hover),
.video-item-biref:not(:hover) .biref-title-line2,
.video-item-biref:not(:hover) .biref-title,
.season-title,
.bangumi-title,
.filter-title,
.filter-item:not(.on),
.sort-item:not(.on) span,
.block-title,
.b-head .b-head-t,
.footer a {
	transition: border .25s, color .25s!important;
	color: var(--m-font-color)!important;
}

.state,
.tag-list a,
.type,
.bangumi-label,
.uname span,
.pub-info,
.filter-name,
span.name {
	color: var(--m-font-subcolor)!important;
}

.desc,
.user-name,
span.username:not(.is-vip),
.item-a:not(:hover),
a.name:not([class*=vip]),
.view-more {
	transition: .25s;
	color: var(--m-font-subcolor2)!important;
}

.is-vip,
.vip,
.vip-red-name,
.this-is-vip {
	color: var(--accent-pink)!important;
}

a.title:hover,
a.t:hover,
.detail a:hover,
.user-name a:hover,
span.username:not(.is-vip):hover,
.user-name:hover,
.headline a.name:hover,
.more:hover,
.operation:hover,
.rank-item:hover .ri-title,
.u-info .name .username:hover,
.uname,
.bangumi-item:hover .bangumi-title,
.footer a:hover,
.season-title:hover {
	transition: color .25s!important;
	color: var(--accent-blue)!important;
}


/*----------------------
        网页顶部
------------------------*/
.nav-mask {
	background-color: var(--m-darkest)!important;
}

.bili-header-m > .nav-menu {
	box-shadow: 0 10px 40px var(--shadow-color);
}

.bili-header-m .nav-menu .nav-con .nav-item {
	box-sizing: border-box;
}

.bili-header-m .nav-menu .nav-con .nav-item::before {
	content: '';
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	height: 4px;
	background-color: var(--m-font-color);
	transform: scalex(0);
	transition: transform .25s ease-in-out;
}

.bili-header-m .nav-menu .nav-con .nav-item:not(.profile-info):hover::before {
	transform: scalex(1)
}

.bili-header-m .nav-menu .nav-con .nav-item:hover {
	background-color: transparent;
}

.bili-header-m .nav-menu .nav-con .nav-item a.t {
	color: var(--m-font-color)!important;
}

.bili-header-m .nav-menu .nav-con .nav-item .i-frame {
	background: transparent!important;
	filter: drop-shadow(0 15px 20px rgba(0, 0, 0, .8))
}

.nav-con .dd-bubble,
.bili-header-m .nav-menu .up-load .up-nav/*投稿*/
,
.bili-header-m .i_menu_login/*登录*/
{
	border: none;
	background: var(--m-darkest)!important;
	transition: .25s;
	box-shadow: var(--shadow60)!important;
}

/*个人信息*/
.bili-header-m .profile-m .member-menu {
	border: none;
}

/*未登录状态的个人介绍*/
.bili-header-m .i_menu_login .reg {
	color: var(--m-font-color);
}

.bili-header-m .i_menu_login .tip {
	color: var(--m-font-subcolor)!important;
}

/*个人介绍*/
.bili-header-m .profile-info:hover {
	border: none!important;
}

.bili-header-m .profile-info .i-face .face {
	border: none!important;
}

.bili-header-m .profile-m.dd-bubble a,
.bili-header-m .profile-m.dd-bubble .header-u-info,
.bili-header-m .profile-m.dd-bubble .member-menu a,
.bili-header-m .profile-m.dd-bubble .member-bottom a {
	color: var(--m-font-color);
	transition: .25s;
}

.bili-header-m .profile-m.dd-bubble .member-menu {
	border-color: var(--m-gray2-solid);
}

.bili-header-m .profile-m .btns-profile .ver .tips {
	border: none;
	background: var(--m-darkest);
	color: var(--m-font-color);
}

.bili-header-m .profile-m .btns-profile .num-tip,
.bili-header-m .profile-m .grade .bar {
	background: var(--m-darkest-solid)!important;
}

.header-u-info .grade .desc-tips {
	background: var(--m-darkest)!important;
}

.header-u-info .grade .desc-tips div {
	color: var(--m-font-subcolor);
}

.header-u-info .grade .desc-tips div strong {
	color: var(--m-font-color)!important;
}

/*调整个人中心，投稿管理，直播中心，b币钱包，订单中心*/
.nav-con .profile-m.dd-bubble .member-menu a {
	transition: .25s!important;
}

.nav-con .profile-m.dd-bubble .member-menu a:hover {
	filter: drop-shadow(0 8px 10px var(--accent-blue));
	color: var(--accent-blue)!important;
}

.nav-con .profile-m.dd-bubble .member-menu a:not(:hover) i {
	filter: brightness(200%)!important;
}

.nav-con .profile-m.dd-bubble .member-bottom {
	background: var(--m-gray)!important;
}

#i_menu_become_vip {
	background: var(--m-darkest);
}

#i_menu_become_vip .recommand .title,
#i_menu_become_vip .recommand .item .recommand-link {
	color: var(--m-font-color);
	transition: .25s;
}

#i_menu_become_vip .recommand .item .recommand-link:hover {
	color: var(--accent-blue);
}

#i_menu_become_vip .recommand .renew-btn button {
	transition: .25s;
	filter: none;
}

#i_menu_become_vip .recommand .renew-btn button:hover {
	background: var(--accent-pink);
}

/*稍后再看，收藏夹，历史列表修改*/
.bili-header-m .mini-wnd-nav .list.history li {
	transition: .25s;
}

.bili-header-m .mini-wnd-nav .list.history li:hover {
	background: var(--m-gray);
}

.bili-header-m .mini-wnd-nav .list.history li.timeline {
	border-color: var(--m-gray2-solid);
	color: var(--m-font-subcolor)!important;
}

.bili-header-m .mini-wnd-nav .list.history li a {
	color: var(--m-font-color);
}

.bili-header-m .mini-wnd-nav .list.history li a:hover {
	color: var(--accent-blue);
}

/*稍后再看，收藏夹，历史按钮调整*/
.bili-header-m .mini-wnd-nav .read-more {
	transition: .25s;
	border: none;
	background: var(--accent-blue);
	color: var(--m-font-color);
}

.bili-header-m .mini-wnd-nav .read-more:hover {
	background: var(--accent-pink);
}

.bili-header-m .mini-wnd-nav .read-more i {
	transition: .25s;
	filter: brightness(500%);
}

.bili-header-m .mini-wnd-nav .read-more:hover i {
	margin-left: 20px;
}

.bili-header-m .mini-wnd-nav .list.history li.timeline .date {
	background: var(--m-darkest);
}

/*投稿*/
.stardust-video .nav-menu .up-load {
	margin-right: 20px;
}

.nav-menu .up-load .u-link {
	background: var(--accent-blue)!important;
	box-shadow: 0 0 20px var(--accent-blue-light);
	transition: .6s;
}

.nav-menu .up-load .u-link:hover {
	box-shadow: 0 0 60px var(--accent-blue-light);
}

.bili-header-m .nav-menu .up-load .up-nav {
	background-color: transparent!important;
}

.bili-header-m .nav-menu .up-load .up-nav li {
	background: var(--m-darkest);
}

.bili-header-m .nav-menu .up-load .up-nav li:hover {
	background: var(--m-gray);
}

.bili-header-m .nav-menu .up-load .up-nav li a {
	color: var(--accent-blue);
}

.bili-header-m .nav-menu .up-load .up-nav li i {
	filter: var(--pink-turn-blue-filter);
}

/*通知与动态数量*/
.bili-header-m .nav-menu .nav-con .nav-item .t .num {
	box-shadow: 0 0 20px var(--accent-pink);
	transition: .25s;
}

.bili-header-m .nav-menu .nav-con .nav-item .t:hover .num {
	animation: .3s shock infinite alternate;
	box-shadow: 0 0 25px 2px var(--accent-pink-light);
}



/*----------------------
        网页主体
------------------------*/
/*Logo效果*/
.head-logo {
	transition: .25s !important;
	background-position: top !important;
}

.head-logo:hover {
    height: 120px!important;
    transform: rotate(-10deg) scale(1.3);
}
/*搜索框*/
.bili-header-m .search {
	background-color: var(--m-darkest);
	transition: .25s;
	padding-left: 26px;
}

.bili-header-m .search:hover {
	background-color: var(--m-darkest-solid)
}

/*排行榜*/
.bili-header-m .search .link-ranking {
	width: 24px;
	overflow: hidden;
	left: 5px;
}

.bili-header-m .search .link-ranking span {
	filter: contrast(0) brightness(2);
	transition: .3s ease-out;
}

.bili-header-m .search .link-ranking:hover span {
	filter: hue-rotate(225deg) brightness(1.2);
	transform: scale(.9) translate(-1px, -3px);
}

.bili-header-m .search .link-ranking,
.search .searchform {
	background-color: transparent!important;
}

.bili-header-m .search .link-ranking:hover {
	background-color: var(--m-gray);
}

.search .search-keyword {
	background-color: transparent;
	border-radius: 4px;
}

.search .searchform input.search-keyword {
	color: var(--m-font-color);
}

.search .searchform input.search-keyword::placeholder,
.nav-search-box .nav-search .nav-search-keyword::placeholder {
	color: transparent;
}

.bili-header-m .search button.search-submit {
	border-radius: 0 5px 5px 0!important;
	filter: contrast(0) brightness(2);
	transition: transform .3s ease-out;
}

.bili-header-m .search button.search-submit:hover {
	filter: hue-rotate(225deg) brightness(1.2);
	transform: scale(.9) rotate(-10deg) translate(-1px, -1px);
}

/*导航栏搜索框*/
#nav_searchform {
	border: none!important;
	background-color: var(--m-gray)!important;
	transition: .25s;
}

#nav_searchform:hover {
	border-color: var(--accent-blue)!important;
}

#nav_searchform input {
	box-sizing: border-box;
	color: var(--m-font-color)!important;
	transition: .25s ease-in-out;
}


#banner_link .bilibili-suggest {
	width: 296px;
	right: 26px;
}

/*搜索建议*/
.bilibili-suggest,
.suggest-wrap {
	animation: .5s showInfo;
	background: var(--m-darkest)!important;
	border-color: transparent!important;
	box-shadow: var(--shadow60)!important;
	overflow:hidden;
	transition: .25s;
	margin-top:5px!important;
	padding-bottom:0!important;
}

/*这里是调整搜索栏的历史搜索那里的*/
.bili-header-m .bilibili-suggest .b-line{
	display:none;/*去除历史搜索，关键词文字与线*/
}

.bilibili-suggest .suggest-item a,
.suggest-wrap .vt-text,
.suggest-wrap .keyword-wrap .keyword {
	color: var(--m-font-color)!important;
}

.bilibili-suggest .suggest-item:hover,
.bilibili-suggest .suggest-item.focus,
.suggest-wrap .vt-text:hover,
.suggest-wrap .vt-text.focus {
	background: var(--m-gray)!important;
}

.bilibili-suggest .suggest-item .cancel,
.suggest-wrap .history-li .clear {
	filter: brightness(200%)!important;
}

.bilibili-suggest .suggest-item .cancel,
.suggest-wrap .history-li .clear {
	transition: transform 3s!important;
}

.bilibili-suggest .suggest-item:hover .cancel,
.suggest-wrap .history-li:hover .clear {
	transform: rotate(1800deg)!important;
}

.bilibili-suggest .suggest-item .cancel:hover,
.suggest-wrap .history-li .clear:hover {
	filter: brightness(100%)!important;
}

/*搜索*/
.search-wrap {
	width: 556px;
}

.search-wrap .search-logo {
	background-image: url(https://i.loli.net/2019/01/25/5c4afa94e490d.png) !important;
	background-position: left bottom !important;
	background-size: 80px;
	background-repeat: no-repeat;
	width: 80px!important;
}

.search-wrap .fixed-top {
	background-color: var(--m-darkest);
	box-shadow: 0 10px 40px var(--shadow-color)
}

.search-wrap .search-button {
	position: relative;
	left: 20px;
}

.search-wrap .search-button span {
