	box-shadow: 0 4px 20px var(--accent-pink-light);
}

.bb-comment .comment-send .comment-submit::before {
	content: '发表评论';
	color: #fff;
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	font-size: 14px;
	padding: 12px 20px;
	z-index: 1;
}

.bb-comment .comment-send .comment-submit::after {
	content: '';
	z-index: 0;
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	background-color: var(--accent-pink);
	transform: scalex(0);
	transform-origin: right center;
	transition: transform .2s ease-in-out;
}

.bb-comment .comment-send .comment-submit:hover::after {
	transform-origin: left center;
	transform: scalex(1);
}

