#bilibiliPlayer {
	background-color: var(--m-darkest-solid);
	border-radius: 0;
	box-shadow: none!important;
}

.bilibili-player-video-message {
	display: none;
}

.bilibili-player-video-sendbar,
.bilibili-player-auxiliary-area {
	background-color: var(--m-darkest-solid)!important;
	border: none!important;
}

/*设置网页宽屏模式下隐藏发射弹幕按钮,鼠标划过显示进度条*/
.bili-wrapper .mode-webfullscreen .bilibili-player-video-sendbar {
	display: none;
}

.bili-wrapper .mode-webfullscreen .bilibili-player-video-control {
	position: absolute!important;
	bottom: 0;
	opacity: 0!important;
	transition: .25s;
}

.mode-webfullscreen .bilibili-player-video-control:hover {
	opacity: 1!important;
}

.mode-webfullscreen .bilibili-player-video-state {
	bottom: 40px!important;
}

.bilibili-player-mode-selection-container/*弹幕选择*/
,
.bilibili-player-color-picker-container/*弹幕颜色*/
{
	background-color: var(--m-darkest);
	border: none;
}

.bilibili-player-mode-selection-row .row-title {
	color: var(--m-font-subcolor)!important;
}

.bilibili-player-mode-selection-row .selection-name {
	color: var(--m-font-color)!important
}

.bilibili-player-color-picker-color-current/*拾色器显示颜色*/
{
	border-color: transparent!important;
}

.bilibili-player-color-picker-input-group input/*拾色器十六进制输入框*/
{
	background-color: var(--m-gray);
	border-color: transparent!important;
	color: var(--m-font-color)!important;
}

.bilibili-player-color-picker-color-current::after {
	color: var(--m-font-color)!important;
}

.bilibili-player-info,
.bilibili-player-filter > div,
.bilibili-player-video-sendbar > [class*=bilibili-player]:not(.bilibili-player-mode-selection-container):not(.bilibili-player-color-picker-container),
[class*="bilibili-player-filter"] {
	border: none!important;
	background: transparent!important;
}

.bilibili-player-video-control > div:not(.bilibili-player-video-control-top) {
	transition: .25s;
}

.bilibili-player-video-control > div:hover i,
.bilibili-player-video-control > div:hover .bpui-selectmenu-txt {
	color: var(--accent-blue)!important;
}

.bilibili-player-video-control > div:hover[class*=btn] {
	background-color: var(--m-gray)!important;
}

.bpui-slider-tracker-wrp/*进度条*/
{
	filter: invert(100) hue-rotate(180deg)
}

.bpui-slider-handle/*控制球*/
{
	background-color: var(--accent-blue)!important;
}

.bilibili-player-video-volumebar-wrp,
.bilibili-player-danmaku-setting-lite,
.bpui-selectmenu-list,
.bilibili-player-video-web-fullscreen,
.bilibili-player-video-control {
	background-color: var(--m-darkest)!important;
	box-shadow: none!important;
	border: none!important;
}

.bilibili-player-video-btn-widescreen {
	background-color: transparent!important;
}

.bilibili-player-danmaku-setting-lite div,
.bilibili-player-danmaku-setting-lite div span {
	color: var(--m-font-color)!important;
}

/*视频画质选择*/
.bpui-selectmenu-list li:hover,
.bpui-selectmenu-list li[data-selected="selected"] {
	background-color: var(--m-gray)!important;
}

.bpui-selectmenu-list li {
	color: var(--m-font-color)!important;
}

.bpui-selectmenu-list li[data-selected="selected"] span:not(.bilibili-player-bigvip) {
	color: var(--accent-blue)!important;
}


/*弹幕输入框*/
.bilibili-player-video-danmaku-input {
	background-color: var(--m-gray)!important;
	color: var(--m-font-color)!important;
	margin-right: 10px;
}

.bilibili-player-video-btn-send span/*发送弹幕*/
{
	color: var(--m-font-color)!important;
}

/*右侧字体调整*/
.bilibili-player-watching-number,
.bilibili-player-recommend-title {
	transition: .2s;
	color: var(--m-font-color)!important;
}

.bilibili-player-filter-btn.active > span,
.bilibili-player-filter-btn.bpui-state-hover > span,
.bilibili-player-recommend-title:hover {
	color: var(--accent-blue)!important;
}

.bilibili-player-filter .bpui-button-text {
	transition: .2s;
	color: var(--m-font-subcolor)!important;
}

/*右侧设置按钮、播放器设置按钮调整*/
.bilibili-player-setting-btn:hover,
.bilibili-player-setting-menu:hover {
	color: var(--m-font-color)!important;
	background-color: transparent!important;
}

/*播放器设置*/
.bilibili-player-setting {
	background-color: var(--m-darkest-solid)!important;
}

.bilibili-player-panel-title,
.bilibili-player-panel-area-title,
.bilibili-player-fl,
.bpui-tab-list-row:not(.bpui-state-selected),
.bpui-button-text-only {
	color: var(--m-font-color)!important;
}

.bilibili-player-panel-label {
	color: var(--m-font-subcolor)!important;
}

.bilibili-player-panel-title,
.bpui-selectmenu,
.bilibili-player-area-separator {
	border-color: var(--m-gray)!important;
}

.bilibili-player-setting-reset {
	color: var(--m-font-subcolor)!important;
	border-color: transparent;
	background-color: var(--m-gray)!important;
}

.bilibili-player-setting-reset:hover {
	border-color: var(--accent-blue)!important;
	color: var(--accent-blue)!important;
	background-color: transparent!important;
}

/*右侧板块*/
.bilibili-player-danmaku-wrap {
	background-color: transparent!important;
}

.bilibili-player-danmaku-function {
	background-color: var(--m-dark-solid)!important;
}

/*选中的弹幕*/
.bilibili-player .bilibili-player-auxiliary-area .bilibili-player-danmaku-wrap .bilibili-player-danmaku-list .danmaku-info-row.bpui-selected,
.bilibili-player .bilibili-player-auxiliary-area .bilibili-player-danmaku-wrap .bilibili-player-danmaku-list .danmaku-info-row.bpui-selected > span,
.bilibili-player .bilibili-player-auxiliary-area .bilibili-player-danmaku-wrap .bilibili-player-danmaku-list .danmaku-info-row.bpui-selecting,
.bilibili-player .bilibili-player-auxiliary-area .bilibili-player-danmaku-wrap .bilibili-player-danmaku-list .danmaku-info-row.bpui-selecting > span {
	background-color: var(--m-gray)!important;
}

/*举报、屏蔽按钮*/
.danmaku-info-report-btn,
.danmaku-info-block-btn {
	background-color: var(--m-darkest-solid)!important;
}

.danmaku-info-report-btn:hover,
.danmaku-info-block-btn:hover {
	background-color: var(--accent-blue)!important;
}

/*查看历史弹幕*/
.bilibili-player-danmaku-btn-history {
	background-color: var(--m-darkest-solid)!important;
	border-top: 1px solid var(--m-gray)!important;
	border-bottom: 1px solid var(--m-gray)!important;
}

.bilibili-player-danmaku-btn-history:hover {
	color: var(--m-font-color)!important;
}

/*屏蔽设定*/
.bilibili-player-block-sync-btn {
	background-color: transparent!important;
}

.bilibili-player .bilibili-player-block .bilibili-player-block-tabpanel .bilibili-player-block-tabpanel-row.special-title .bpui-button-text .bpui-checkbox-text {
	color: var(--m-font-color)!important;
}

.bilibili-player-block-sync-btn .bpui-button-text {
	color: var(--accent-blue)!important;
}

/*屏蔽面板*/
.bilibili-player-block-tabpanel {
	border-color: var(--m-gray)!important;
}

/*下拉屏蔽类型列表*/
.bilibili-player-block-string-type,
.bpui-selectmenu-list.bpui-selectmenu-list-left {
	background-color: var(--m-gray);
	border: none;
	box-shadow: none;
}

.bpui-selectmenu.selectmenu-mode-absolute .bpui-selectmenu-txt {
	color: var(--m-font-color)!important;
}

/*屏蔽关键词输入框*/
.bilibili-player-block-string-short {
	color: var(--m-font-color);
	background-color: var(--m-gray);
	border-color: var(--m-gray)!important;
}

/*屏蔽词，屏蔽用户*/
.bilibili-player-block-type-user,
.bilibili-player-block-type-keyword {
	transition: .25s;
}

.bilibili-player-block-type-user:hover,
.bilibili-player-block-type-keyword:hover {
	color: var(--m-font-color)!important;
}

.bilibili-player-block-tabpanel-row.active-1 .bilibili-player-block-type-keyword,
.bilibili-player-block-tabpanel-row.active-2 .bilibili-player-block-type-user {
	color: var(--m-font-color)!important;
	background-color: var(--m-gray)!important;
	border: none!important;
}

/*屏蔽内容*/
.bilibili-player-block-tabpanel-row.bilibili-player-border {
	background-color: var(--m-gray)!important;
	border: none!important;
}

.bilibili-player-block-tabpanel-title {
	color: var(--m-font-color)!important;
}

.bilibili-player-block-filter-type .bilibili-player-block-filter-image {
	color: var(--m-font-subcolor)!important;
}

.bilibili-player-block-filter-type.disabled {
	background-color: transparent!important;
}

.bilibili-player-block-filter-type.disabled .bilibili-player-block-filter-image,
.bilibili-player-block-filter-type.disabled .bilibili-player-block-filter-image *,
.bilibili-player-block-filter-type.disabled .bilibili-player-block-filter-label {
	color: var(--accent-pink)!important;
}

.bilibili-player-block-filter-type .bilibili-player-block-filter-label {
	color: var(--m-font-subcolor2)!important;
}

/*mini播放器调整*/
.mini-player,
#bofqi {
	transition: transform .2s, box-shadow .2s;
}

.mini-player:active {
	transform: scale(1.1);
	box-shadow: 0 20px 80px var(--shadow-dark-color)!important;
	opacity: .85;
}

/*发nm光*/
.mini-player-move,
.drag-bar {
	box-shadow: 0 20px 80px #FFF!important;
	filter: invert(1) hue-rotate(180deg)
}

.mini-player::before,
#bofqi::before {
	box-shadow: 0 20px 80px var(--shadow-dark-color)!important;
}

/*右侧边按钮，播放器问题反馈，播放器调整*/
.bgray-btn-wrap div {
	background-color: var(--m-gray);
	border: none;
}

.bgray-btn-wrap div:hover {
	color: var(--m-font-color);
}

/*右下按钮调整，mini播放器*/
.bangumi-nav-right > div,
#fixnav_report > div,
.fixed-nav-m > div,
/*解除区域设置插件*/
#balh-settings-btn {
	transition: background-color .25s;
	background-color: var(--m-gray)!important;
	color: var(--m-font-subcolor)!important;
	border: none!important;
}

.bangumi-nav-right > div:hover,
#fixnav_report > div:hover,
#balh-settings-btn:hover,
.fixed-nav-m > div:hover {
	background-color: var(--accent-blue)!important;
	color: var(--m-font-color)!important;
}

/*底部*/
.footer {
	display: none;
}

/***************旧版视频界面****************/
@media screen and (max-width: 1400px) {
	.bili-wrapper {
		width: 980px!important;
	}
}

@media screen and (min-width: 1400px) {
	.bili-wrapper {
		width: 1160px!important;
	}
}

/*视频加载中*/
.core-player-video-panel,
.bilibili-player .bilibili-player-area .bilibili-player-video-wrap .bilibili-player-video-panel {
	background-color: var(--m-darkest-solid);
}

.core-player-video-panel-image-detail {
	filter: invert(1);
}

/*板块颜色调整*/
div[style="display:none"] + .bili-wrapper,
.bili-wrapper[class*=report-wrap-module]:not(#__bofqi):not(.multi-page):not(.elecrank-wrapper),
.video-top-info,
.ex-wrapper,
.video-toolbar-module,
#arc_toolbar_report {
	background-color: var(--m-darkest)!important;
	border: none;
	border-radius: 0;
}

.l-wrapper {
	background-color: var(--m-darkest);
	margin-bottom: 40px;
}

/*视频左边内容*/
.y-layout,
.ex-wrapper > div {
	border-color: var(--m-gray);
	margin: 0 20px;
}

#viewbox_report,
.video-info-module {
	padding: 20px 0 20px 20px;
	background-color: transparent;
	width: 620px;
}

#v_upinfo,
.up-info-module {
	background-color: transparent;
	padding: 20px 20px 20px 0!important;
	margin: 0;
}

.up-info-m .sign .bi-btn,
/*直播中*/
.up-info-m .u-face .live-online:not(:hover) {
	filter: invert(1) hue-rotate(180deg)
}


/*选择视频章节*/
#v_multipage {
	background-color: var(--m-darkest);
	margin-bottom: 20px;
	padding: 20px 20px 10px 20px!important;
}

.multi-page a.item {
	transition: .2s!important;
	margin-right: 12px;
	background-color: var(--m-gray);
	color: var(--m-font-color);
	border-color: transparent;
}

#v_multipage .v-part-toggle/*收起、打开分p*/
{
	top: 20px!important;
	right: 20px;
}


#playpage_share:hover/*分享按钮*/
,
.video-toolbar-module .share-box:hover {
	background-color: transparent;
	border-color: transparent;
}

.video-toolbar-module .share-box::after {
	background: transparent!important;
}

.video-toolbar-module .share-box .share-popup .share-address ul li input/*视频地址文本框*/
{
	color: var(--m-font-color);
	background: var(--m-gray)!important;
	border: none;
}

/*分享、收藏、投币板块*/
#arc_toolbar_report .share-btn li/*分享平台图标*/
,
.video-toolbar-module .share-box .share-btn ul li,
.elecrank-btn .elecrank-bg/*充电*/
{
	filter: invert(100) hue-rotate(180deg) brightness(1.2)
}

#arc_toolbar_report span.t,
.video-toolbar-module span.t {
	color: var(--m-font-color)!important;
}

.video-toolbar-module .num {
	color: var(--m-font-subcolor)!important;
}

.share-popup {
	background-color: var(--m-darkest)!important;
	border: none!important;
}

/*收藏、投币、稍后看*/
.btn-item i,
.btn-item b {
	filter: invert(100) hue-rotate(180deg);
}

/*去除手机上观看与下载客户端按钮*/
#playpage_mobileshow,
.app-download,
.app-box {
	display: none;
}

/*助手调整*/
#arc_toolbar_report > .bilibili-helper > span {
	color: var(--m-font-subcolor2);
}

#arc_toolbar_report > .bilibili-helper:hover > span {
	color: var(--accent-blue);
}

/*活动信息*/
.activity-m .inside-wrp {
	background-color: var(--m-darkest);
	border: none;
	color: var(--m-font-color);
	margin: 0;
	margin-bottom: 20px;
	border-radius: var(--content-radius) var(--content-radius) 0 0;
}

.activity-m .inside-wrp .inside-bg:before {
	display: none!important;
}

/*标签信息*/
#v_tag li {
	transition: .2s;
	border:none;
}

#v_tag li:not(:hover) {
	border-color: var(--m-gray);
	background-color: var(--m-gray)!important;
}

#v_tag li > a {
	color: var(--m-font-subcolor2);
}

#v_tag li:hover{
	background-color:var(--m-gray2);
}

/*添加视频标签*/
.s_tag .tag-area > a:not(:hover) {
	border-color: transparent!important;
}

.s_tag .tag-area > a:hover {
	background-color: transparent!important;
}

/*视频标签*/
.tag-info-pane {
	background-color: var(--m-darkest)!important;
	box-shadow: 0 0 0 2px var(--content-border-color), var(--shadow60)!important;
	border-radius: var(--content-radius);
}

.tag-info-pane .btn-right-box {
	border: none;
}

.tag-info-pane a {
	color: var(--m-font-color)!important;
}

.tag-info-pane a:hover {
	color: var(--accent-blue)!important;
}

.tag-info-pane a.btn-close:hover,
.tag-info-pane a.btn-report:hover {
	filter: none;
	background-color: var(--m-gray)!important;
}

.tag-info-pane .btn-subscribe {
	transition: .25s;
	color: var(--accent-pink)!important;
	background-color: transparent!important;
	border: 1px solid var(--accent-pink);
}

.tag-info-pane .btn-subscribe:hover {
	color: #FFF!important;
	background-color: var(--accent-pink)!important;
}

/*标签修改记录、查看标签使用说明*/
.s_tag .btn-view-tag a {
	color: var(--m-font-subcolor);
}

.video-desc-m .reprint/*未经作者授权 禁止转载*/
,
.video-desc .reprint {
	color: var(--accent-pink);
}

.video-desc-m .info {
	color: var(--m-font-subcolor)!important;
}

/*去广*/
.video-bottom-ad {
	display: none;
}

/*看过该视频的还喜欢*/
.also-like .also-like__title {
	color: var(--m-font-color);
}

#recommend_report,
.also-like {
	transform: scale(.95);
	margin: 0!important;
}

/*充电榜*/
.elecrank-wrapper {
	background-color: var(--m-darkest)!important;
	padding: 0px;
}

.elecrank-wrapper [class*=elecrank] {
	background-color: transparent!important;
	border: none;
}

.elecrank-header .name,
.elecrank-wrapper .elecrank-header .name {
	background-color: transparent;
	color: var(--accent-blue)!important;
}

.elecrank-header .name:hover {
	color: var(--accent-pink)!important;
}

/*充电用户名*/
.elecrank-wrapper .elecrank-content .rank-item .item-name {
	color: var(--m-font-color);
}

/*充电用户名留言*/
.elecrank-wrapper .elecrank-content .rank-item .msg {
	color: var(--m-font-subcolor);
	background-color: var(--m-gray);
}

.elecrank-wrapper .elecrank-content .rank-item .msg .arrow {
	filter: brightness(.2);
}

/*大家都在看的直播*/
#live_recommand_report {
	display: none;
}

/***************旧版番剧播放页面****************/
/*番剧信息*/
#primary_menu {
	margin-bottom: 0!important;
}

#bangumi_header {
	background-color: var(--m-darkest)!important;
	border-radius: var(--content-radius);
}

#bangumi_header .header-info {
	padding-left: 20px;
}

.bangumi-up-info {
	background-color: transparent;
}

.bangumi-up-info .info .user .message {
	color: var(--m-font-subcolor);
	transition: .25s;
}

.up-info a {
	color: var(--m-font-subcolor2)!important
}

.up-info a:hover {
	color: var(--accent-blue)!important
}

.header-pay-banner {
	padding-right: 20px;
}

/*大会员专享播放权限*/
.btn-pay-wrapper.is-vip a {
	filter: invert(1) hue-rotate(180deg);
}

.bangumi-player {
	box-shadow: none!important;
	background-color: transparent!important;
}

#bangumi_detail {
	background-color: var(--m-darkest);
	border: none;
	margin-bottom: 20px;
	z-index: 1;
}

#bangumi_detail .share-module {
	width: auto!important;
}

.bangumi-info .info-right {
	transform: scale(.96)
}

.bangumi-info .info-right .info-title h2 {
	color: var(--m-font-color)
}

.bangumi-info .info-cover {
	padding-left: 20px;
}

/*助手调整*/
#bangumi_detail .info-title .func-module .bilibili-helper > span {
	color: var(--m-font-color);
	background-color: var(--accent-blue);
	border: none;
	box-shadow: 0 2px 20px var(--accent-blue);
}

#bangumi_detail .info-title .func-module .bilibili-helper > span:hover {
	box-shadow: 0 4px 40px var(--accent-blue);
}


/*承包*/
.btn-sponsor-wrapper span:not(.sponsor-count),
.main-container .sponsor-wrapper .sponsor-right-area .sponsor-count-info span {
	color: var(--m-font-color)!important
}
.main-container .sponsor-wrapper .sponsor-right-area .sponsor-count-info {
	color: var(--m-font-subcolor);
}
.share-list {
	width: auto;
	float: right;
	filter: invert(100) hue-rotate(180deg);
}
/*用手机看*/
.share-module .share-list li.share-btn.btn-app {
	display: none;
}
.bangumi-list-wrapper .top-block {
	border: none!important;
}
/*列表方向控制键*/
.slider-contro span {
	background-color: var(--m-gray)!important;
}
.slider-contro i {
	display: none!important;
}
/*剧情列表*/
.episode-list li,
.episode-list p,
.episode-list h4 {
	transition: .2s;
}
.episode-list li:not(.on) {
	background-color: transparent!important;
	border-color: var(--m-gray);
}
.episode-list li:not(.on):hover {
	border-color: var(--accent-blue);
}
.episode-list li h4 {
	color: var(--m-font-color)!important;
}
.episode-list li:hover:not(.on) h4 {
	color: var(--accent-blue)!important;
}
/*剧评*/
#bangumi_media {
	background-color: var(--m-darkest)!important;
	border: none;
	margin-bottom: 20px;
	border-radius: 0 0 var(--content-radius) var(--content-radius);
}
#bangumi_media h4 {
	color: var(--m-font-color);
	padding-left: 20px;
}
/*写点评*/
.btn-go-write {
	color: var(--m-font-subcolor2)!important;
	transition: .25s;
}
/*查看更多点评*/
